import React, { Component } from "react";
import { Route, Link } from "react-router-dom";
import "./style.css";
import * as trademarkApi from "../apis/trademark";

function importAll(r) {
  return r.keys().map(r);
}

const images = importAll(
  require.context("../../Asset/trademark", false, /\.(png|jpe?g|svg)$/)
);

const myImage = (name) => {
  let img = null;
  images.map((data) => {
    if (data.default.includes(name)) img = data.default;
    return data.default;
  });
  return img;
};

export default class index extends Component {
   mapTrademark = async (type) => {
    let result = null;
    let trademark = [];
    await trademarkApi.trademark(type)
    .then((success) => {
      if (success.status===200) {
        let temp = [success.data.value.$values];
        for (var i=0; i<temp.length; i++) {
          trademark.push(temp[i].tenthuonghieu);
        }
      }
    })
    .catch((error) => {
      console.error(error);
      return;
    });
    console.log("trademark"+trademark);

    if (trademark.length !== 0) {
      result = trademark.map((value, index) => {
        return (
          <Link to={"/" + type + "/" + value} key={index} className="trademark-item">
            <img
              className="trademark-icon loaded"
              // data-ll-status="loaded"
              src={myImage(value)}
              alt=""
            ></img>
          </Link>
        );
      });
    }
    return <Route>{result}</Route>;
  };
  // eslint-disable-next-line react/require-render-return
  async render() {
    return (
      <div className="trademark-list">
        {await this.mapTrademark(this.props.type)}
      </div>
    );
  }
}
