import React, { Component } from "react";
import * as productApi from "../../apis/product";
import * as detailProductapi from "../../apis/detailProduct";
import * as imageApi from "../../apis/image";
import {Redirect} from "react-router-dom";

export default class SanphamItem extends Component {
  state = { sanpham: {} , giamoi:0};
  async componentDidMount(){
      let sanpham ={};
      let giamoi = 0;
    await productApi.findProductById(this.props.masanpham)
    .then(success => {
        if (success.status === 200){
            console.log(success.data.value);
           sanpham= success.data.value;
        }
    })
    .catch(error => {
        console.error(error);
    })

    await detailProductapi.xemgiatheomasanpham(this.props.masanpham)
    .then(success => {
      giamoi=success.data.value;
    })
    .catch((error) => {
      console.error(error);
    });

    this.setState({sanpham:sanpham, giamoi: giamoi})
  }

  render() {
    return (
      <li className="item border rounded">
        <button className="btn-delete">×</button>
        <div className="thumbnail">
          <a  href ={this.state.sanpham.image}>
            <div className="Picture__StyledPicture-sc-10icj7e-0 jDowEZ loaded">
              <img
              alt ="anhsanpham"
                className="image"
                src={this.state.sanpham!==undefined && imageApi.image(this.state.sanpham.image)}
                onClick={()=><Redirect to ={this.this.state.sanpham.image}></Redirect>}
              />
            </div>
          </a>
        </div>
        <div className="body">
          <a
            className="name"
            href={()=>false}
            onClick={()=><Redirect to ={this.this.state.sanpham.image}></Redirect>}
          >
            {this.state.sanpham.tensanpham}
          </a>
          <div className="rating">
            <div className="rating__base">
              <svg
                stroke="currentColor"
                fill="currentColor"
                strokeWidth={0}
                viewBox="0 0 24 24"
                height="1em"
                width="1em"
                xmlns="http://www.w3.org/2000/svg"
