        data-aos="zoom-in"
        data-aos-delay={100}
      >
        <div className="icon-box iconbox-blue">
          <div className="box-item-sticker-percent">
            <p>Giảm {((100-this.state.giamoi/this.props.product.dongia*100).toFixed())}%</p>
          </div>
          <Link to={this.props.product.image} className="box-click" >
