import React, { Component } from "react";
import "./style.css";
import SlideShow from "../SlideShow";
import * as mobileApi from "../apis/mobile";
import * as detailProductApi from "../apis/detailProduct";
import * as imageApi from "../apis/image";
import { bindActionCreators } from "redux";
import { connect } from "react-redux";
import * as cartAct from "../redux/actions/cartAct";
import PropTypes from "prop-types";
import * as favoriteApi from "../apis/favorite";
// import { indexOf } from "lodash";
import { withRouter } from "react-router-dom";

function Mota(props) {
  return <h1>Mô tả</h1>;
}

function Thongso(props) {
  return <h1>Thông số kỹ thuật</h1>;
}

function Danhgia(props) {
  return <h1>Đánh giá</h1>;
}

class index extends Component {
  state = {
    liked: false,
    product: {},
    sohinhanh: 0,
    active: 0,
    dongia: 0,
    dungluong: "",
    mausac: "",
    chondungluong: "-1",
    chonmausac: "-1",
  };
  renderTab(n) {
    switch (n) {
      case 0:
        return <i className="material-icons">Mô tả</i>;
      case 1:
        return <i className="material-icons">Thông số kỹ thuật</i>;
      case 2:
        return <i className="material-icons">Đánh giá</i>;
      default:
        return;
    }
  }
  click = (n) => {
    this.setState({ active: n });
  };
  renderItem(n) {
    switch (n) {
      case 0:
        return <Mota></Mota>;
      case 1:
        return <Thongso></Thongso>;
      case 2:
        return <Danhgia></Danhgia>;
      default:
        return;
    }
  }

  changeLike() {
    if (this.props.customer === undefined) {
      this.props.history.push("/login");
      return;
    }
    if (this.state.liked) {
      favoriteApi
        .unlike(this.props.customer.makhachhang, this.state.product.masanpham)
        .then((success) => {
          if (success.status === 200) {
            this.setState({ liked: false });
          }
        })
        .catch((error) => {
          console.error(error);
        });
      return;
    }

    favoriteApi
      .like(this.props.customer.makhachhang, this.state.product.masanpham)
      .then((success) => {
        if (success.status === 200) {
          this.setState({ liked: true });
        }
      })
      .catch((error) => {
        console.error(error);
      });
    return;
  }
  xemGia() {
    var { loai } = this.props.match.params;
    var { hang } = this.props.match.params;
    var { dong } = this.props.match.params;
    var { sanpham } = this.props.match.params;

    var request = {
      loai: loai,
      hang: hang,
      dong: dong,
      sanpham: sanpham,
      dungluong: this.state.chondungluong,
      mausac: this.state.chonmausac,
    };

    detailProductApi
      .xemgiatheodungluongvamausac(request)
      .then((success) => {
        if (success.status === 200) {
          this.setState({
            dongia: success.data.value,
          });
        }
      })
      .catch((error) => {
        console.error(error);
      });
  }

  async loadDungluong() {
    var { loai } = this.props.match.params;
    var { hang } = this.props.match.params;
    var { dong } = this.props.match.params;
    var { sanpham } = this.props.match.params;
    var query;
    query = `/${loai}/${hang}/${dong}/${sanpham}/dungluong?mausac=${this.state.chonmausac}`;
    await detailProductApi
      .dungluong(query)
      .then((success) => {
        if (success.status === 200) {
          this.setState({ dungluong: success.data.value.$values });
        }
      })
      .catch((error) => {
        console.error(error);
      });
  }

  async loadMausac() {
    var { loai } = this.props.match.params;
    var { hang } = this.props.match.params;
    var { dong } = this.props.match.params;
    var { sanpham } = this.props.match.params;
    var query;
    query = `/${loai}/${hang}/${dong}/${sanpham}/mausac?dungluong=${this.state.chondungluong}`;
    await detailProductApi
      .mausac(query)
      .then((success) => {
        if (success.status === 200) {
          this.setState({ mausac: success.data.value.$values });
        }
      })
      .catch((error) => {
        console.error(error);
      });
  }

  async checked() {
    if (this.props.customer === undefined) return;
    await favoriteApi
      .checklike(this.props.customer.makhachhang, this.state.product.masanpham)
      .then((success) => {
        if (success.status === 200) {
          this.setState({ liked: true });
        }
      })
      .catch((error) => {
        console.error(error);
      });
  }

  async componentDidMount() {
    var { loai } = this.props.match.params;
    var { hang } = this.props.match.params;
    var { dong } = this.props.match.params;
    var { sanpham } = this.props.match.params;

    this.loadDungluong();
    this.loadMausac();

    var query = `/${loai}/${hang}/${dong}/${sanpham}`;
    await mobileApi
      .mobileInfo(query)
      .then((success) => {
        if (success.status === 200) {
          this.setState({
            product: success.data.value,
            sohinhanh: success.data.serializerSettings.sohinhanh - 1,
          });
        }
      })
      .catch((error) => {
        console.error("error" + error);
      });
    await this.xemGia();
    await this.checked();
  }
  addToCart = () => {
    let { product } = this.state;
    let { cartActionCreators } = this.props;

    cartActionCreators.addToCartReport(
      {
        makhachhang: this.props.customer.makhachhangNavigation.makhachhang,
        masanpham: product.masanpham,
        dungluong: this.state.dungluong[this.state.chondungluong].dungluong,
        mausac: this.state.mausac[this.state.chonmausac].mausac,
      },
      this.props.history
    );
  };
  render() {
    return (
      <section>
        <div className="container">
          <div className="detail-product__box-name">
            <div className="cps-container">
              <div className="box-name__box-product-name">
                <h1>{this.state.product.tensanpham} </h1>
              </div>
              <div className="box-name__box-raiting">
                <i className="fas fa-star checked" />
                <i className="fas fa-star checked" />
                <i className="fas fa-star checked" />
                <i className="fas fa-star checked" />
                <i className="fas fa-star checked" />
                &nbsp;14 đánh giá
              </div>
            </div>
          </div>

          <div className="container">
            <div className="row">
              <div className="col-xs-6 col-sm-6 col-md-6 col-lg-6">
                <SlideShow
                  product={this.state.product}
                  sohinhanh={this.state.sohinhanh}
                ></SlideShow>
              </div>
              <div className="col-xs-6 col-sm-6 col-md-6 col-lg-6">
                <div id="price" className="price mt-5">
                  {this.state.dongia}₫
                </div>
                <div className="box-linked">
                  <div className="box-title">
                    <p className="box-title__title">Chọn dung lượng</p>
                  </div>
                  <div className="list-linked">
                    {function () {
                      var result = [];
                      result = this.state.dungluong.map((value, key) => {
                        return (
                          <a
                            className={
                              key ===
                              (() => {
                                var i = 0;
                                for (; i < this.state.dungluong.length; i++) {
                                  if (
                                    this.state.dungluong[i].dungluong ===
                                    this.state.chondungluong
                                  )
                                    break;
                                }
                                return i;
                              })()
                                ? "item-linked box-shadow selected"
                                : "item-linked box-shadow"
                            }
                            href={() => false}
                            key={key}
                            onClick={async () => {
                              if (
                                key ===
                                (() => {
                                  var i = 0;
                                  for (; i < this.state.dungluong.length; i++) {
                                    if (
                                      this.state.dungluong[i].dungluong ===
                                      this.state.chondungluong
                                    )
                                      break;
                                  }
                                  return i;
                                })()
                              ) {
                                await this.setState({ chondungluong: "-1" });
                                if (this.state.chonmausac === "-1")
                                  this.xemGia();
                              } else
                                await this.setState({
                                  chondungluong:
                                    this.state.dungluong[key].dungluong,
                                });
                              this.loadMausac();
                              if (this.state.chonmausac === "-1") return;

                              this.xemGia();
                            }}
                          >
                            <strong>{value.dungluong}</strong>
                          </a>
                        );
                      });

                      return result;
                    }.bind(this)()}
                  </div>
                </div>

                <div className="box-product-option">
                  <div className="box-title">
                    <p className="box-title__title">Chọn màu</p>
                  </div>
                  <div className="box-content">
                    <ul id="configurable_swatch_color" className="list-colors">
                      {function () {
                        var result = [];
                        result = this.state.mausac.map((value, key) => {
                          return (
                            <li
                              key={key}
                              id="option161"
                              className={
                                key ===
                                (() => {
                                  var i = 0;
                                  for (; i < this.state.mausac.length; i++) {
                                    if (
                                      this.state.mausac[i].mausac ===
                                      this.state.chonmausac
                                    )
                                      break;
                                  }
                                  return i;
                                })()
                                  ? "item-color option-b-c  wide-swatch swatch box-shadow selected"
                                  : "item-color option-b-c  wide-swatch swatch box-shadow "
                              }
                            >
                              <a
                                name="b-c"
                                id="swatch161"
                                className="swatch-link swatch-link-80"
                                title={value.mausac}
                                href={() => false}
                                alt={value.mausac}
                                onClick={async () => {
                                  if (
                                    key ===
                                    (() => {
                                      var i = 0;
                                      for (
